    for (int i = 0; i < n; i += chunkSize) {
      chunks[i / chunkSize] = new long[chunkSize];
      System.arraycopy(arr, i, chunks[i / chunkSize], 0, chunkSize);
    }

    // Sort each chunk individually
    for (int i = 0; i < chunks.length; i++) {
      Arrays.sort(chunks[i]);
    }

    // Recombine the sorted chunks
    long[] result = new long[n];
    int j = 0;
    for (int i = 0; i < n; i += chunkSize) {
      for (int k = 0; k < chunkSize; k++) {
        result[i + k] = chunks[j][k];
      }
      j++;
    }

    // Copy the sorted result back to the original array
    System.arraycopy(result, 0, arr, 0, n);
  }
}
```
