    plan_type: datafusion::logical_expr::PlanType,
    indent_level: u32,
) -> StringifiedPlan {
    match plan_type {
        datafusion::logical_expr::PlanType::LogicalAnd => {
            let left_plan = self.to_stringified(datafusion::logical_expr::PlanType::LogicalOr, indent_level - 1);
            let right_plan = self.to_stringified(datafusion::logical_expr::PlanType::LogicalOr, indent_level - 1);
            StringifiedPlan::new(datafusion::logical_expr::PlanType::LogicalAnd, format!("{}{} {}",
