            let children_plans = self.to_stringified(
                plan_type, 
                indent_level - 1
            );
            StringifiedPlan::new(datafusion::logical_expr::PlanType::LogicalOr, format!("{}{} {}", 
                "||", 
                children_plans.indentation(), 
                "\n"))
        }
        // Add more cases for other plan types as needed
    }
}
