    ```rust
use parking_lot::Mutex;
use std::{sync::Arc, thread};

fn main() {
    let mutex = Mutex::new(0);

    let task1 = Arc::new(task1_mutex(mutex.clone()));
    let task2 = Arc::new(task2_mutex(mutex.clone()));

    tokio::spawn(task1);
    tokio::spawn(task2);
}

async fn task1_mutex(mutex: Arc<Mutex<i32>>) {
