            Self { table: vec![] }
        }

        async fn get_partition(&mut self, key: &str) -> usize {
            // Calculate the hash value of the input key
            let mut hash_value = 0;
            for c in key.chars() {
                hash_value += c as i32;
            }

            // Find the corresponding partition using consistent hashing
            for (index, entry) in self.table.iter().enumerate() {
                if hash_value <= index as i32 {
                    return *entry.1.parse::<usize>().unwrap();
                } else if hash_value > (self.table.len() - 1 + index as i32) {
                    return *entry.1.parse::<usize>().unwrap();
                }
            }

            // If the input key is not found, add it to the table and return the next available partition
            self.table.push((self.table.len() as i32, hash_value.to_string()));
            0
        }
