pub struct ShutdownNotifier {
    notify_shutdown: Sender<()>,
    shutdown_complete_rx: Receiver<()>,
    shutdown_complete_tx: Sender<()>,
}

fn main() {
    let (notify_shutdown, shutdown_complete_rx) = mpsc::channel();

