fn is_subdirectory(path: &PathBuf, base_path: &PathBuf) -> bool {
    let parent = path.parent();
    let base = base_path.file_name().unwrap();

    match parent {
        Some(p) if p.starts_with(&base) => true,
        _ => false,
    }
}
```
