    for key in keys.iter() {
        assert_ne!(
            consistent_hash.get(key.as_bytes()).unwrap().name(),
            nodes[0].name()
        );
    }
    consistent_hash.add(node, num_replicas);
    // Add node back to the hash ring with its original number of replicas
    // This ensures that the workload is balanced across all nodes again
}
```

